import './App.css';
// import SearchIcon from './search.svg'
import { useState, useEffect } from 'react'

function Search(){
  const [urlInput, setUrlInput] = useState('')
  // const [requestData, setRequestData] = useState({})
  const [responseData, setResponseData] = useState({})
  function handleChange(event) {
     const { value } = event.target
     setUrlInput(value)
  }

  function handleSubmit(event) {

    const url = new URL(urlInput)
    const searchParams = new URLSearchParams(url.search)
    const ytid = searchParams.get('v')
    sendRequest(ytid)
    event.preventDefault()
  }

  async function sendRequest(ytid){
    try {
      const response = await fetch(`http://127.0.0.1:5000/?ytid=${ytid}`)
      const jsonData = await response.json()
      setResponseData(jsonData)
    } catch (error) {
      console.error('Error fetching data:', error)
    }
  }

  useEffect(() => {
    console.log(responseData)
  }, [responseData])

  return (
    <div className='parent'>
      {/* <svg className="searchbar--icon" src={SearchIcon}></svg> */}
      <div className="searchbar--icon"></div>
      <form onSubmit={handleSubmit}>
        <input className="searchbar--input" type="text" onChange={handleChange} value={urlInput}/>
      </form>
    </div>
  )
}

function App() {
  return (
    <div className="content">
      <p>Enter a YouTube URL</p>
      <p>&nbsp;</p>
      <Search/>
    </div>
  );
}

export default App;
