import './App.css';
import { useState, useEffect } from 'react'
import { ReactComponent as SeachSvg } from './search.svg'

function Search({handleResult}){
  const [urlInput, setUrlInput] = useState('')
  // const [requestData, setRequestData] = useState({})
  const [responseData, setResponseData] = useState({})

  function handleChange(event) {
     const { value } = event.target
     setUrlInput(value)
  }

  function handleSubmit(event) {
    if (!urlInput){
      return
    }
    console.log(urlInput)

    try {
      const url = new URL(urlInput)
      const searchParams = new URLSearchParams(url.search)
      const ytid = searchParams.get('v')
      sendRequest(ytid)
      event.preventDefault()
    } catch (error) {
      console.log(error)
      alert("Not a valid Youtube video!")
    }
  }

  async function sendRequest(ytid){
    try {
      const response = await fetch(`http://127.0.0.1:5000/?ytid=${ytid}`)
      const jsonData = await response.json()
      setResponseData(jsonData)
    } catch (error) {
      console.error('Error fetching data:', error)
    }
  }

  // change this as you move the state for result up
  useEffect(() => {
    if (responseData.ytid || responseData.ytid === 0){
      handleResult(true)
    } else {
      handleResult(false)
    }

  }, [responseData])

  console.log(hasResult)

  return (
    <>
      <div className='parent'>
        <form onSubmit={handleSubmit}>
          <button className="searchbar--icon" type='submit'><SeachSvg/></button>
          <input className="searchbar--input" type="text" onChange={handleChange} value={urlInput}/>
        </form>
      </div>
      {/* <div style={{height: "100px"}}></div>
      <div className='result' style={{display: hasResult ? "flex" : "none"}}>
        {responseData.ytid === 0 ? "This video is not in AudioSet" : "This video is in AudioSet"}
      </div> */}
    </>
  )
}

function App() {
  const [hasResult, setHasResult] = useState(false)

  const handleResult = (bool) => {
    setHasResult(bool)
  }

  return (
    <div className='container'>
      <div className='content'>
          <div className='above'>
            <h1>Is it in AudioSet?</h1>
            <p>Enter a YouTube URL</p>
          </div>
        <Search handleResult={handleResult}/>
        <div className='result' style={{display: hasResult ? "flex" : "none"}}>
        {responseData.ytid === 0 ? "This video is not in AudioSet" : "This video is in AudioSet"}
      </div>
      </div>
    </div>
  );
}

export default App;
